"use strict";
debugger
const Compiler = require("./Compiler");
const MultiCompiler = require("./MultiCompiler");
const NodeEnvironmentPlugin = require("./node/NodeEnvironmentPlugin");
const WebpackOptionsApply = require("./WebpackOptionsApply");
const WebpackOptionsDefaulter = require("./WebpackOptionsDefaulter");
const validateSchema = require("./validateSchema");
const WebpackOptionsValidationError = require("./WebpackOptionsValidationError");
const webpackOptionsSchema = require("../schemas/WebpackOptions.json");
const RemovedPluginError = require("./RemovedPluginError");
const version = require("../package.json").version;

/** @typedef {import("../declarations/WebpackOptions").WebpackOptions} WebpackOptions */

/**
 * @param {WebpackOptions} options options object
 * @param {function(Error=, Stats=): void=} callback callback
 * @returns {Compiler | MultiCompiler} the compiler object
 */
const webpack = (options, callback) => {
	console.log("start:进入webpack.js 编译开始！")
	console.log("1：----验证config配置参数是否有误----")
	const webpackOptionsValidationErrors = validateSchema(
		webpackOptionsSchema,
		options
	);
	if (webpackOptionsValidationErrors.length) {
		console.log("1：----config配置参数有误----")
		throw new WebpackOptionsValidationError(webpackOptionsValidationErrors);
	}
	let compiler;
	console.log("2：----准备实例化compiler----")
	if (Array.isArray(options)) {
		console.log("2：----config参数是数组，开始new MultiCompiler----")
		compiler = new MultiCompiler(
			Array.from(options).map(options => webpack(options))
		);
	} else if (typeof options === "object") {
		console.log("2：----config参数是对象，开始 new WebpackOptionsDefaulter 获取默认配置----")
		options = new WebpackOptionsDefaulter().process(options);
		compiler = new Compiler(options.context);
		compiler.options = options;
		console.log("2：----config参数是对象，已实例compiler，将配置参数挂在compiler下----")
		new NodeEnvironmentPlugin({
			infrastructureLogging: options.infrastructureLogging
		}).apply(compiler);
		if (options.plugins && Array.isArray(options.plugins)) {
			console.log("3：----处理config配置中的plugins数组，遍历----")
			for (const plugin of options.plugins) {
				if (typeof plugin === "function") {
					console.log("3：----plugin是函数，先执行以下函数，返回一个----")
					plugin.call(compiler, compiler);
				} else {
					console.log("3：----plugin不是函数，触发plugin的apply方法----")
					plugin.apply(compiler);
				}
			}
		}
		console.log("hooks：----environment，在初始化配置文件中的插件之后立即调用，在 compiler environment 准备时调用----")
		compiler.hooks.environment.call();
		console.log("hooks：----afterEnvironment，在 environment 钩子之后立即调用，在 compiler environment 完成设置时调用----")
		compiler.hooks.afterEnvironment.call();
		compiler.options = new WebpackOptionsApply().process(options, compiler);
	} else {
		throw new Error("Invalid argument: options");
	}
	if (callback) {
		if (typeof callback !== "function") {
			throw new Error("Invalid argument: callback");
		}
		if (
			options.watch === true ||
			(Array.isArray(options) && options.some(o => o.watch))
		) {
			const watchOptions = Array.isArray(options)
				? options.map(o => o.watchOptions || {})
				: options.watchOptions || {};
			return compiler.watch(watchOptions, callback);
		}
		compiler.run(callback);
	}
	return compiler;
};

exports = module.exports = webpack;
exports.version = version;

webpack.WebpackOptionsDefaulter = WebpackOptionsDefaulter;
webpack.WebpackOptionsApply = WebpackOptionsApply;
webpack.Compiler = Compiler;
webpack.MultiCompiler = MultiCompiler;
webpack.NodeEnvironmentPlugin = NodeEnvironmentPlugin;
// @ts-ignore Global @this directive is not supported
webpack.validate = validateSchema.bind(this, webpackOptionsSchema);
webpack.validateSchema = validateSchema;
webpack.WebpackOptionsValidationError = WebpackOptionsValidationError;

const exportPlugins = (obj, mappings) => {
	console.log("export:----在webpack.js中将所有插件类型导出为，<key,function>的形式")
	for (const name of Object.keys(mappings)) {
		Object.defineProperty(obj, name, {
			configurable: false,
			enumerable: true,
			get: mappings[name]
		});
	}
};

